import React from "react";
import ReactDOM from "react-dom/client";

const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];

function App() {
    return (
    <div>
        <Header />
        <Menu />
        <Footer />
    </div>

    )
}

function Header() {
    return <h1>Fast React Pizza Co</h1>
}


function Menu() {
    return (
        <div>
            <h2>Our menu</h2>
            <Pizza />
            <Pizza />
            <Pizza />
        </div>
    )
}

function Pizza() {
  return (
      <div>
        <img src="pizzas/spinaci.jpg" alt="spinaci"/>
        <h2>Pizza!</h2>
        <p>ingredients</p>
      </div>
  )
}


function Footer() {
    const currentTime = new Date().toLocaleTimeString()

    return <footer>It's {currentTime}. {getOpenMessage}</footer>
    // return React.createElement('footer', null, "We're currently open!")
}

function getOpenMessage() {
    const hour = new Date().getHours();
    const openHour = 12;
    const closeHour = 22;
    const isOpen = hour >= openHour && hour <= closeHour
    if (isOpen) {
        return "We're currently open!";
    }
    else {
        return "We're currently closed!";
    }
}

// React v18
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
    // optional but useful for development
    <React.StrictMode>
        <App />
    </React.StrictMode>
);


